import axios from "axios";

export const getAll = async () => {
    let temp = await axios.get("http://localhost:8080/facilities");
    return temp.data;
}

export const getById = async (id) => {
    let temp = await axios.get("http://localhost:8080/facilities/" + id);
    return temp.data
}

export const search = async ()

export const deleteById = async (id) => {
    let temp = await axios.delete("http://localhost:8080/facilities/" + id)
        .then(value => {
            return true;
        })
        .catch(reason => {
            console.log(reason);
            return false;
        })
}

export const create = async (data) => {
    let temp = await axios.post("http://localhost:8080/facilities", data)
        .then(value => {
            return true;
        })
        .catch(reason => {
            console.log(reason);
            return false;
        })
}

export const edit = async (id, data) => {
    let temp = await axios.put(`http://localhost:8080/facilities/${id}`, data)
        .then(value => {
            return true;
        })
        .catch(reason => {
            console.log(reason);
            return false;
        })
}

export const getAllRentalType = async () => {
    let temp = await axios.get("http://localhost:8080/rentalTypes");
    return temp.data;
}
