import React, {useEffect, useState} from "react";
import {ErrorMessage, Field, Form, Formik} from "formik";
import "../../../css/form.css"
import * as FacilityService from "../../../service/FacilityService"
import {ToastContainer, toast} from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import {useNavigate} from "react-router";
import * as Yup from "yup"

function FacilityCreate() {
    const navigate = useNavigate();
    const [form, setForm] = useState({
        "name": "",
        "usageArea": 0,
        "rentalCost": 0,
        "maxGuest": 0,
        "rentalType": {}
    });
    const [rentalTypes, setRentalTypes] = useState([]);
    useEffect(() => {
        getALlRentalType();
    }, []);
    const getALlRentalType = async () => {
        let temp = await FacilityService.getAllRentalType();
        setRentalTypes(temp);
    };
    const createFacility = async (data) => {
        let isSuccess = await FacilityService.create(data);
        return isSuccess;
    }
    const notifySuccess = (txt) => {
        toast.success(txt, {
            position: "bottom-right",
            autoClose: 2500,
            hideProgressBar: false,
            closeOnClick: true,
            pauseOnHover: true,
            draggable: true,
            progress: undefined,
            theme: "light",
        });
    }
    const handleValidate = {
        rentalType: Yup.object()
            .shape({
                id: Yup.number().required(),
                name: Yup.string().required()
            })
            .required("Require select!")
            .default(null)
    }
    const handleSubmit = (values) => {
        console.log(values);
        values = {...values, "rentalType": JSON.parse(values.rentalType)}
        let isSuccess = createFacility(values);
        if (isSuccess) {
            notifySuccess("Create successfully!");
        }
    }
    return (
        <div className="create-form">
            <Formik initialValues={form} validationSchema={Yup.object(handleValidate)}
                    onSubmit={(values, {resetForm}) => {
                        handleSubmit(values);
                        resetForm();
                    }}>
                <Form>
                    <div className="form-group">
                        <label htmlFor="name" className="form-label">Name</label>
                        <Field type="text" className="form-control item" id="name" placeholder="Name" name="name"/>
                    </div>
                    <div className="form-group">
                        <label htmlFor="usageArea" className="form-label">Usage Area</label>
                        <Field type="number" className="form-control item" id="usageArea" placeholder="Usage Area"
                               name="usageArea"/>
                    </div>
                    <div className="form-group">
                        <label htmlFor="rentalCost" className="form-label">Rental Cost</label>
                        <Field type="number" className="form-control item" id="rentalCost" placeholder="Rental Cost"
                               name="rentalCost"/>
                    </div>
                    <div className="form-group">
                        <label htmlFor="maxGuest" className="form-label">Max Guest</label>
                        <Field type="number" className="form-control item" id="maxGuest" placeholder="Max Guest"
                               name="maxGuest"/>
                    </div>
                    <div className="form-group">
                        <label htmlFor="rentalType" className="form-label">Rental Type</label>
                        <Field as="select" className="form-control item" id="rentalType" placeholder="Rental Type"
                               name="rentalType">
                            <option value={JSON.stringify({})}>Choose an option</option>
                            {rentalTypes.map((value, index) => {
                                return (
                                    <option key={index} value={JSON.stringify(value)}>{value.name}</option>
                                )
                            })}
                        </Field>
                        <ErrorMessage name="rentalType" component="span" className="text-danger"></ErrorMessage>
                    </div>
                    <div className="form-group">
                        <button type="submit" className="btn btn-block create-account">Create</button>
                    </div>
                </Form>
            </Formik>
            <ToastContainer/>
        </div>
    )
}

export default FacilityCreate;
