import React, {useEffect, useState} from "react";
import * as FacilityService from "../../../service/FacilityService"

function Facility() {
    const [facilities, setFacilities] = useState([]);
    const getALlFacility = async () => {
        let temp = await FacilityService.getAll();
        setFacilities(temp);
    }
    useEffect(() => {
        getALlFacility();
    }, [])
    return (
        <div className="container">
            <div className="row col-md-6 col-md-offset-2 custyle">
                <a href="#" className="btn btn-primary btn-xs pull-right">Add new Facility</a>
                <table className="table table-striped custab mx-auto">
                    <thead>
                    <tr>
                        <th>Name</th>
                        <th>Usage Area</th>
                        <th>Rental Cost</th>
                        <th>Max Guest</th>
                        <th>Rental Type</th>
                        <th className="text-center">Action</th>
                    </tr>
                    </thead>
                    <tbody>
                    {facilities.map((value, index) => {
                        return (
                            <tr key={index}>
                                <td>{value.name}</td>
                                <td>{value.usageArea}</td>
                                <td>{value.rentalCost}</td>
                                <td>{value.maxGuest}</td>
                                <td>{value.rentalType}</td>
                                <td className="text-center">
                                    <a className='btn btn-info btn-xs' href="#">Edit</a>
                                    <a href="#" className="btn btn-danger btn-xs"> Del</a>
                                </td>
                            </tr>
                        )
                    })}
                    </tbody>
                </table>
            </div>
        </div>
    )
}

export default Facility;
