import React, {useEffect, useState} from "react";
import * as FacilityService from "../../../service/FacilityService"
import ReactPaginate from 'react-paginate';
import FacilityCreate from "./FacilityCreate";
import FacilityModalDelete from "./FacilityModalDelete";

function Facility() {
    const [idSelected, setIdSelected] = useState(-1);
    const [change, setChange] = useState(false);
    const [facilities, setFacilities] = useState([]);
    const [search, setSearch] = useState({
        name: "",
        rentalTypeId: 0
    });
    const [rentalTypes, setRentalTypes] = useState([]);
    const getALlRentalType = async () => {
        let temp = await FacilityService.getAllRentalType();
        setRentalTypes(temp);
    };
    const getAllBySearch = async (name, rentalTypeId) => {
        let temp = await FacilityService.search(name, rentalTypeId);
        setFacilities(temp);
    };
    const getALlFacility = async () => {
        let temp = await FacilityService.getAll();
        setFacilities(temp);
    };
    const handleDelete = (id) => {
        setFacilities(prevState => {
            return facilities.filter(value => value.id !== id);
        });
    };
    const handleChangeSearch = (evt) => {
        setSearch(prevState => {
            return {...prevState, [evt.target.name]: evt.target.value};
        });
        console.log(search);
    };
    const handleSearch = (e) => {
        getAllBySearch(search.name, search.rentalTypeId);
        e.preventDefault();
    }

    function Items({currentItems}) {
        return (
            <>
                {currentItems.map((value, index) => {
                    return (
                        <tr key={index}>
                            <td>{value.name}</td>
                            <td>{value.usageArea}</td>
                            <td>{value.rentalCost}</td>
                            <td>{value.maxGuest}</td>
                            <td>{value.rentalType.name}</td>
                            <td className="text-center">
                                <button onClick={() => setIdSelected(value.id)}
                                        className="btn btn-primary btn-xs">Edit
                                </button>
                                <FacilityModalDelete handle={handleDelete} object={value}/>
                            </td>
                        </tr>
                    )
                })}
            </>
        );
    }

    function PaginatedItems({itemsPerPage}) {
        const [itemOffset, setItemOffset] = useState(0);
        const endOffset = itemOffset + itemsPerPage;
        const currentItems = facilities.slice(itemOffset, endOffset);
        const pageCount = Math.ceil(facilities.length / itemsPerPage);

        const handlePageClick = (event) => {
            const newOffset = (event.selected * itemsPerPage) % facilities.length;
            setItemOffset(newOffset);
        };

        return (
            <>
                <Items currentItems={currentItems}/>
                <tr>
                    <td colSpan="100%" className="Page navigation example">
                        <ReactPaginate
                            breakLabel="..."
                            nextLabel="next>"
                            onPageChange={handlePageClick}
                            pageRangeDisplayed={5}
                            pageCount={pageCount}
                            previousLabel="<previous"
                            renderOnZeroPageCount={null}
                            containerClassName="pagination justify-content-center"
                            activeLinkClassName="page-link"
                            pageLinkClassName="page-link"
                            activeClassName="active"
                            previousClassName="page-iem"
                            previousLinkClassName="page-link"
                            nextClassName="page-iem"
                            nextLinkClassName="page-link"
                        />
                    </td>
                </tr>
            </>
        );
    };
    useEffect(() => {
        getALlFacility();
        getALlRentalType();
    }, [change]);
    return (
        <div className="container-fluid">
            <FacilityCreate id={idSelected} change={change} setChange={setChange}/>
            <div className="mx-auto row col-md-6 col-md-offset-2 custyle">
                <div>
                    <form action="">
                        <input placeholder="name" onChange={(e) => handleChangeSearch(e)} type="text" name="name"/>
                        <select name="rentalTypeId" onChange={(e) => handleChangeSearch(e)}>
                            <option value={0}>Choose an option</option>
                            {
                                rentalTypes.map(value => {
                                    return (
                                        <option key={value.id} value={value.id}>{value.name}</option>
                                    )
                                })
                            }
                        </select>
                        <button type="submit" onClick={(e) => handleSearch(e)}>Search</button>
                    </form>
                </div>
                <button onClick={() => setIdSelected(-1)} className="btn btn-primary btn-xs pull-right">Add new
                    Facility
                </button>
                <table className="table table-striped custab">
                    <thead>
                    <tr>
                        <th>Name</th>
                        <th>Usage Area</th>
                        <th>Rental Cost</th>
                        <th>Max Guest</th>
                        <th>Rental Type</th>
                        <th className="text-center">Action</th>
                    </tr>
                    </thead>
                    <tbody>
                    <PaginatedItems itemsPerPage={5}/>
                    </tbody>
                </table>
            </div>
        </div>
    )
}

export default Facility;
