import React, {useEffect, useState} from "react";
import * as FacilityService from "../../../service/SongService"
import ReactPaginate from 'react-paginate';
import FacilityCreate from "./FacilityCreate";

function Song() {
    const [idSelected, setIdSelected] = useState(-1);
    const [change, setChange] = useState(false);
    const [song, setSong] = useState([]);
    const [search, setSearch] = useState({
        name: ""
    });
    const [rentalTypes, setRentalTypes] = useState([]);
    const getALlRentalType = async () => {
        let temp = await FacilityService.getAllRentalType();
        setRentalTypes(temp);
    };
    const getAllBySearch = async (name) => {
        let temp = await FacilityService.search(name, rentalTypeId, startDate, endDate);
        setSong(temp);
    };
    const getALlFacility = async () => {
        let temp = await FacilityService.getAll();
        setSong(temp);
    };
    const handleDelete = (id) => {
        setSong(prevState => {
            return song.filter(value => value.id !== id);
        });
    };
    const handleChangeSearch = (evt) => {
        setSearch(prevState => {
            return {...prevState, [evt.target.name]: evt.target.value};
        });
        console.log(search);
    };
    const handleSearch = (e) => {
        getAllBySearch(search.name, search.rentalTypeId, search.startDate, search.endDate);
        e.preventDefault();
    }

    function Items({currentItems}) {
        return (
            <>
                {currentItems.map((value, index) => {
                    return (
                        <tr key={index}>
                            <td>{value.id}</td>
                            <td>{value.name}</td>
                            <td>{value.time}</td>
                            <td>{value.like_amount}</td>
                            <td>{value.status.name}</td>
                            <td className="text-center">
                                <button onClick={() => setIdSelected(value.id)}
                                        className="btn btn-primary btn-xs">CÃ´ng khai
                                </button>
                            </td>
                        </tr>
                    )
                })}
            </>
        );
    }

    function PaginatedItems({itemsPerPage}) {
        const [itemOffset, setItemOffset] = useState(0);
        const endOffset = itemOffset + itemsPerPage;
        const currentItems = song.slice(itemOffset, endOffset);
        const pageCount = Math.ceil(song.length / itemsPerPage);

        const handlePageClick = (event) => {
            const newOffset = (event.selected * itemsPerPage) % song.length;
            setItemOffset(newOffset);
        };

        return (
            <>
                <Items currentItems={currentItems}/>
                <tr>
                    <td colSpan="100%" className="Page navigation example">
                        <ReactPaginate
                            breakLabel="..."
                            nextLabel="next>"
                            onPageChange={handlePageClick}
                            pageRangeDisplayed={5}
                            pageCount={pageCount}
                            previousLabel="<previous"
                            renderOnZeroPageCount={null}
                            containerClassName="pagination justify-content-center"
                            activeLinkClassName="page-link"
                            pageLinkClassName="page-link"
                            activeClassName="active"
                            previousClassName="page-iem"
                            previousLinkClassName="page-link"
                            nextClassName="page-iem"
                            nextLinkClassName="page-link"
                        />
                    </td>
                </tr>
            </>
        );
    };
    useEffect(() => {
        getALlFacility();
        getALlRentalType();
    }, [change]);
    return (
        <div className="container-fluid">
            <div className="mx-auto row custyle">
                <div className="bg-light p-2">
                    <form action="" className="row g-1">
                        <div className="col-4">
                            <label htmlFor="name" className="form-label">Name</label>
                            <input className="form-control" placeholder="name" onChange={(e) => handleChangeSearch(e)}
                                   type="text" id="name" name="name"/>
                        </div>
                        <div className="col-4">
                            <label htmlFor="name" className="form-label">Rental Type</label>
                            <select className="col-4 form-control" id="rentalTypeId" name="rentalTypeId"
                                    onChange={(e) => handleChangeSearch(e)}>
                                <option value={0}>Choose an option</option>
                                {
                                    rentalTypes.map(value => {
                                        return (
                                            <option key={value.id} value={value.id}>{value.name}</option>
                                        )
                                    })
                                }
                            </select>
                        </div>
                        <div className="col-4">
                            <label htmlFor="dateStart" className="form-label">Date Start</label>
                            <input className="form-control" onChange={(e) => handleChangeSearch(e)}
                                   type="date" id="dateStart" name="startDate"/>
                        </div>
                        <div className="col-4">
                            <label htmlFor="dateEnd" className="form-label">Date End</label>
                            <input className="form-control" onChange={(e) => handleChangeSearch(e)}
                                   type="date" id="dateEnd" name="endDate"/>
                        </div>
                    </form>
                    <button className="btn btn-primary col-2 m-2" type="submit"
                            onClick={(e) => handleSearch(e)}>Search
                    </button>
                </div>
                <button onClick={() => setIdSelected(-1)} className="btn btn-primary btn-xs pull-right">Add new
                    Facility
                </button>
                <table className="table table-striped custab">
                    <thead>
                    <tr>
                        <th>Name</th>
                        <th>Usage Area</th>
                        <th>Rental Cost</th>
                        <th>Max Guest</th>
                        <th>Rental Type</th>
                        <th className="text-center">Action</th>
                    </tr>
                    </thead>
                    <tbody>
                    <PaginatedItems itemsPerPage={5}/>
                    </tbody>
                </table>
            </div>
        </div>
    )
}

export default Song;
