import React, {useState} from 'react';
import Button from 'react-bootstrap/Button';
import Modal from 'react-bootstrap/Modal';
import * as FacilityService from "../../../service/SongService"
import {ToastContainer, toast} from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import * as SongService from "../../../service/SongService";

function SongModalPublic(props) {
    const {handle, object} = props;
    const [show, setShow] = useState(false);

    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);

    const notifySuccess = (txt) => {
        toast.success(txt, {
            position: "bottom-right",
            autoClose: 2500,
            hideProgressBar: false,
            closeOnClick: true,
            pauseOnHover: true,
            draggable: true,
            progress: undefined,
            theme: "light",
        });
    }

    const editSong = async (id, data) => {
        let isSuccess = await SongService.edit(id, data);
        return isSuccess;
    };

    function handleDelete() {
        editSong(object.id, object);
        handleClose();
    }

    return (
        <>
            <Button variant="primary" onClick={handleShow}>
                Công khai
            </Button>

            <Modal
                show={show}
                onHide={handleClose}
                backdrop="static"
                keyboard={false}
            >
                <Modal.Header closeButton>
                    <Modal.Title>Confirm Delete</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    Bạn có muốn công khai bài hát "{object.name}"
                </Modal.Body>
                <Modal.Footer>
                    <Button variant="secondary" onClick={handleClose}>
                        Không
                    </Button>
                    <Button variant="danger" onClick={handleDelete}>Có</Button>
                </Modal.Footer>
            </Modal>
        </>
    );
}

export default SongModalPublic;
