import React, {useEffect, useState} from "react";
import * as SongService from "../../../service/SongService"
import ReactPaginate from 'react-paginate';
import SongCreate from "./SongCreate";
import SongModalPublic from "./SongModalPublic";
import {NavLink} from "react-router-dom";

function Song() {
    const [idSelected, setIdSelected] = useState(-1);
    const [change, setChange] = useState(false);
    const [song, setSong] = useState([]);
    const [search, setSearch] = useState({
        name: ""
    });
    const getAllBySearch = async (name) => {
        let temp = await SongService.search(name);
        setSong(temp);
    };
    const getALlSong = async () => {
        let temp = await SongService.getAll();
        setSong(temp);
    };
    const editSong = async (id, data) => {
        await SongService.edit(id, data);
    };
    const handlePublic = (id, data) => {
        let temp = {
            ...data, status: {
                id: 1,
                name: "CÃ´ng khai"
            }
        };
        editSong(id, temp);
        getALlSong();
    };
    const handleChangeSearch = (evt) => {
        setSearch(prevState => {
            return {...prevState, [evt.target.name]: evt.target.value};
        });
        console.log(search);
    };
    const handleSearch = (e) => {
        getAllBySearch(search.name);
    }

    function Items({currentItems}) {
        return (
            <>
                {currentItems.map((value, index) => {
                    return (
                        <tr key={index} onClick={() => setIdSelected(value.id - 1)}>
                            <td>{value.id}</td>
                            <td>{value.name}</td>
                            <td>{value.singer}</td>
                            <td>{value.time}</td>
                            <td>{value.like_amount}</td>
                            <td>{value.status.name}</td>
                            <td className="text-center">
                                <SongModalPublic handle={handlePublic} object={value}/>
                            </td>
                        </tr>
                    )
                })}
            </>
        );
    }

    function PaginatedItems({itemsPerPage}) {
        const [itemOffset, setItemOffset] = useState(0);
        const endOffset = itemOffset + itemsPerPage;
        const currentItems = song.slice(itemOffset, endOffset);
        const pageCount = Math.ceil(song.length / itemsPerPage);

        const handlePageClick = (event) => {
            const newOffset = (event.selected * itemsPerPage) % song.length;
            setItemOffset(newOffset);
        };

        return (
            <>
                <Items currentItems={currentItems}/>
                <tr>
                    <td colSpan="100%" className="Page navigation example">
                        <ReactPaginate
                            breakLabel="..."
                            nextLabel="next>"
                            onPageChange={handlePageClick}
                            pageRangeDisplayed={5}
                            pageCount={pageCount}
                            previousLabel="<previous"
                            renderOnZeroPageCount={null}
                            containerClassName="pagination justify-content-center"
                            activeLinkClassName="page-link"
                            pageLinkClassName="page-link"
                            activeClassName="active"
                            previousClassName="page-iem"
                            previousLinkClassName="page-link"
                            nextClassName="page-iem"
                            nextLinkClassName="page-link"
                        />
                    </td>
                </tr>
            </>
        );
    };
    useEffect(() => {
        getALlSong();
    }, [change]);
    return (
        <div className="container-fluid">
            <div className="mx-auto row custyle p-0">
                <div className="bg-light p-2">
                    <div className="row">
                        <div className="col-4">
                            <strong>{idSelected !== -1 ? song[idSelected].name : ""}</strong>
                            <h3>{idSelected !== -1 ? song[idSelected].singer : ""}</h3>
                            {idSelected !== -1 ? <button className="btn btn-primary">PhÃ¡t nháº¡c</button> : ""}
                        </div>
                        <div className="col-2"></div>
                        <div className="col-6">
                            <label htmlFor="name" className="form-label">TÃªn bÃ i hÃ¡t</label>
                            <input className="form-control" onChange={(e) => handleChangeSearch(e)}
                                   type="text" id="name" name="name"/>
                            <button className="btn btn-primary col-2 m-2" type="submit"
                                    onClick={(e) => handleSearch(e)}>TÃ¬m kiáº¿m
                            </button>
                        </div>
                    </div>
                </div>
                <NavLink to="create" className="btn btn-primary btn-xs pull-right">
                    ÄÄng kÃ½ bÃ i hÃ¡t
                </NavLink>
                <table className="table table-striped custab">
                    <thead>
                    <tr>
                        <th>STT</th>
                        <th>TÃªn bÃ i hÃ¡t</th>
                        <th>Ca sÄ©</th>
                        <th>Thá»i gian phÃ¡t</th>
                        <th>Sá» lÆ°á»£t yÃªu thÃ­ch</th>
                        <th>Tráº¡ng thÃ¡i</th>
                        <th className="text-center">Chá»©c nÄng</th>
                    </tr>
                    </thead>
                    <tbody>
                    <PaginatedItems itemsPerPage={5}/>
                    </tbody>
                </table>
            </div>
        </div>
    )
}

export default Song;
