import React, {useEffect, useState} from "react";
import {ErrorMessage, Field, Form, Formik} from "formik";
import "../../../css/form.css"
import * as SongService from "../../../service/SongService"
import {ToastContainer, toast} from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import * as Yup from "yup"
import {useParams} from "react-router";

function SongCreate() {
    const {id} = useParams("id")
    const formInit = {
        "name": "",
        "singer": "",
        "time": "",
        "like_amount": 0,
        "status": ""
    };
    const [form, setForm] = useState(formInit);
    const [stauts, setStatus] = useState([]);
    useEffect(() => {
        getALlStatus();
        if (id !== -1) {
            getFacilityById(id);
        } else {
            setForm(formInit)
        }
    }, [id]);
    const getALlStatus = async () => {
        let temp = await SongService.getAllStatus();
        setStatus(temp);
    };
    const getFacilityById = async (id) => {
        let temp = await SongService.getById(id);
        temp = {...temp, "status": JSON.stringify(temp.rentalType)}
        setForm(prevState => {
            return {...prevState, ...temp};
        })
    }
    const createFacility = async (data) => {
        let isSuccess = await SongService.create(data);
        return isSuccess;
    }
    const editFacility = async (id, data) => {
        let isSuccess = await SongService.edit(id, data);
        return isSuccess;
    }
    const notifySuccess = (txt) => {
        toast.success(txt, {
            position: "bottom-right",
            autoClose: 2500,
            hideProgressBar: false,
            closeOnClick: true,
            pauseOnHover: true,
            draggable: true,
            progress: undefined,
            theme: "light",
        });
    };
    const handleValidate = {
    }
    const handleSubmit = (values) => {
        values = {
            ...values,
            "status": JSON.parse(values.rentalType)
        }
        let isSuccess = id === -1 ? createFacility(values) : editFacility(id, values);
        if (isSuccess) {
            notifySuccess("Save successfully!");
        } else {
            toast.warn("Save failed!");
        }
        console.log(values);
    }
    return (
        <div className="create-form">
            <Formik initialValues={form} validationSchema={Yup.object(handleValidate)} enableReinitialize={true}
                    onSubmit={(values, {resetForm}) => {
                        handleSubmit(values);
                        resetForm();
                    }}>
                <Form>
                    <legend>{id === -1 ? "Add new Song" : "Edit Song: " + form.name}</legend>
                    <div className="form-group">
                        <label htmlFor="name" className="form-label">Tên bài hát</label>
                        <Field type="text" className="form-control item" id="name" placeholder="Name" name="name"/>
                        <ErrorMessage name="name" component="span" className="text-danger"></ErrorMessage>
                    </div>
                    <div className="form-group">
                        <label htmlFor="usageArea" className="form-label">Usage Area</label>
                        <Field type="number" className="form-control item" id="usageArea" placeholder="Usage Area"
                               name="usageArea"/>
                    </div>
                    <div className="form-group">
                        <label htmlFor="rentalCost" className="form-label">Rental Cost</label>
                        <Field type="number" className="form-control item" id="rentalCost" placeholder="Rental Cost"
                               name="rentalCost"/>
                    </div>
                    <div className="form-group">
                        <label htmlFor="maxGuest" className="form-label">Max Guest</label>
                        <Field type="number" className="form-control item" id="maxGuest" placeholder="Max Guest"
                               name="maxGuest"/>
                    </div>
                    <div className="form-group">
                        <label htmlFor="rentalType" className="form-label">Rental Type</label>
                        <Field as="select" className="form-control item" id="rentalType" placeholder="Rental Type"
                               name="rentalType">
                            <option value="">Choose an option</option>
                            {stauts.map((value, index) => {
                                return (
                                    <option key={index} value={JSON.stringify(value)}>{value.name}</option>
                                )
                            })}
                        </Field>
                        <ErrorMessage name="rentalType" component="span" className="text-danger"></ErrorMessage>
                    </div>
                    <div className="form-group">
                        <label htmlFor="dateExample" className="form-label">Date Example start</label>
                        <Field type="date" className="form-control item" id="dateExample" name="dateExample"/>
                        <ErrorMessage name="dateExample" component="span" className="text-danger"></ErrorMessage>
                    </div>
                    <div className="form-group">
                        <label htmlFor="dateExample2" className="form-label">Date Example end</label>
                        <Field type="date" className="form-control item" id="dateExample2" name="dateExample2"/>
                        <ErrorMessage name="dateExample2" component="span" className="text-danger"></ErrorMessage>
                    </div>
                    <div className="form-group">
                        <button type="submit" className="btn btn-block create-account">Save</button>
                    </div>
                </Form>
            </Formik>
            <ToastContainer/>
        </div>
    )
}

export default SongCreate;
