import React, {useEffect, useState} from "react";
import {ErrorMessage, Field, Form, Formik} from "formik";
import "../../../css/form.css"
import * as SongService from "../../../service/SongService"
import {ToastContainer, toast} from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import * as Yup from "yup"
import {useParams} from "react-router";

function SongCreate() {
    const formInit = {
        "name": "",
        "singer": "",
        "time": "",
        "like_amount": 0,
        "status": ""
    };
    const [form, setForm] = useState(formInit);
    const [stauts, setStatus] = useState([]);
    useEffect(() => {
        getALlStatus();
    }, []);
    const getALlStatus = async () => {
        let temp = await SongService.getAllStatus();
        setStatus(temp);
    };
    const createSong = async (data) => {
        let isSuccess = await SongService.create(data);
        return isSuccess;
    }
    const notifySuccess = (txt) => {
        toast.success(txt, {
            position: "bottom-right",
            autoClose: 2500,
            hideProgressBar: false,
            closeOnClick: true,
            pauseOnHover: true,
            draggable: true,
            progress: undefined,
            theme: "light",
        });
    };
    const handleValidate = {}
    const handleSubmit = (values) => {
        values = {
            ...values,
            "status": JSON.parse(JSON.stringify({
                "id": 1,
                "name": "Công khai"
            })),
            "like_amount": 0
        }
        console.log(values);
        let isSuccess = createSong(values);
        if (isSuccess) {
            notifySuccess("Save successfully!");
        } else {
            toast.warn("Save failed!");
        }
    }
    return (
        <div className="create-form">
            <Formik initialValues={form} validationSchema={Yup.object(handleValidate)} enableReinitialize={true}
                    onSubmit={(values, {resetForm}) => {
                        handleSubmit(values);
                        resetForm();
                    }}>
                <Form>
                    <legend>Add new Song</legend>
                    <div className="form-group">
                        <label htmlFor="name" className="form-label">Tên bài hát</label>
                        <Field type="text" className="form-control item" id="name" placeholder="Name" name="name"/>
                        <ErrorMessage name="name" component="span" className="text-danger"></ErrorMessage>
                    </div>
                    <div className="form-group">
                        <label htmlFor="usageArea" className="form-label">Ca sĩ</label>
                        <Field type="text" className="form-control item" id="usageArea" placeholder="Usage Area"
                               name="usageArea"/>
                    </div>
                    <div className="form-group">
                        <label htmlFor="rentalCost" className="form-label">Nhạc sĩ</label>
                        <Field type="text" className="form-control item" id="rentalCost" placeholder="Rental Cost"
                               name="rentalCost"/>
                    </div>
                    <div className="form-group">
                        <label htmlFor="maxGuest" className="form-label">Thời gian phát</label>
                        <Field type="number" className="form-control item" id="maxGuest" placeholder="Max Guest"
                               name="maxGuest"/>
                    </div>
                    <div className="form-group">
                        <button type="submit" className="btn btn-block create-account">Save</button>
                    </div>
                </Form>
            </Formik>
            <ToastContainer/>
        </div>
    )
}

export default SongCreate;
