import axios from "axios";

export const getAll = async () => {
    let temp = await axios.get("http://localhost:8080/song");
    return temp.data;
}

export const search = async (name) => {
    let isFirstParam = true;
    let path = "http://localhost:8080/song";
    if (name !== "") {
        path += ((isFirstParam) ? "?" : "&") + "name_like=" + name;
        isFirstParam = false;
    }
    console.log(path)
    let temp = await axios.get(path);
    return temp.data;
}

export const deleteById = async (id) => {
    let temp = await axios.delete("http://localhost:8080/song/" + id)
        .then(value => {
            return true;
        })
        .catch(reason => {
            console.log(reason);
            return false;
        })
}

export const create = async (data) => {
    let temp = await axios.post("http://localhost:8080/song", data)
        .then(value => {
            return true;
        })
        .catch(reason => {
            console.log(reason);
            return false;
        })
}

export const edit = async (id, data) => {
    let temp = await axios.put(`http://localhost:8080/song/${id}`, data)
        .then(value => {
            return true;
        })
        .catch(reason => {
            console.log(reason);
            return false;
        })
}

export const getAllStatus = async () => {
    let temp = await axios.get("http://localhost:8080/status");
    return temp.data;
}
