import React, {useEffect, useState} from "react";
import {ErrorMessage, Field, Form, Formik} from "formik";
import "../../../css/form.css"
import * as SongService from "../../../service/SongService"
import {ToastContainer, toast} from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import * as Yup from "yup"
import {useNavigate, useParams} from "react-router";
import {NavLink} from "react-router-dom";

function SongCreate() {
    const navigate = useNavigate();
    const formInit = {
        "name": "",
        "singer": "",
        "artist": "",
        "time": "",
        "like_amount": 0,
        "status": {
            id: 2,
            name: "Lưu trữ"
        }
    };
    const [form, setForm] = useState(formInit);
    const createSong = async (data) => {
        let isSuccess = await SongService.create(data);
        return isSuccess;
    };
    const notifySuccess = (txt) => {
        toast.success(txt, {
            position: "bottom-right",
            autoClose: 2500,
            hideProgressBar: false,
            closeOnClick: true,
            pauseOnHover: true,
            draggable: true,
            progress: undefined,
            theme: "light",
        });
    };
    const handleValidate = {
        name: Yup.string()
            .required("Bắt buộc nhập!")
            .max(30, "Tối đa 30 ký tự!"),
        singer: Yup.string()
            .required("Bắt buộc nhập!")
            .max(30, "Tối đa 30 ký tự!"),
        artist: Yup.string()
            .required("Bắt buộc nhập!"),
        time: Yup.string()
            .required("Bắt buộc nhập!"),
    }
    const handleSubmit = (values) => {
        console.log(values);
        let isSuccess = createSong(values);
        if (isSuccess) {
            notifySuccess("Save successfully!");
        } else {
            toast.warn("Save failed!");
        }
        navigate("/song");
    }
    return (
        <div className="create-form">
            <NavLink to="/song" className="btn btn-primary">Trở về kho nhạc</NavLink>
            <Formik initialValues={form} validationSchema={Yup.object(handleValidate)} enableReinitialize={true}
                    onSubmit={(values, {resetForm}) => {
                        handleSubmit(values);
                        resetForm();
                    }}>
                <Form>
                    <legend>Đăng ký bài hát</legend>
                    <div className="form-group">
                        <label htmlFor="name" className="form-label">Tên bài hát</label>
                        <Field type="text" className="form-control item" id="name" name="name"/>
                        <ErrorMessage name="name" component="span" className="text-danger"></ErrorMessage>
                    </div>
                    <div className="form-group">
                        <label htmlFor="singer" className="form-label">Ca sĩ</label>
                        <Field type="text" className="form-control item" id="singer"
                               name="singer"/>
                        <ErrorMessage name="singer" component="span" className="text-danger"></ErrorMessage>
                    </div>
                    <div className="form-group">
                        <label htmlFor="artist" className="form-label">Nhạc sĩ</label>
                        <Field type="text" className="form-control item" id="artist"
                               name="artist"/>
                        <ErrorMessage name="artist" component="span" className="text-danger"></ErrorMessage>
                    </div>
                    <div className="form-group">
                        <label htmlFor="time" className="form-label">Thời gian phát</label>
                        <Field type="text" className="form-control item" id="time"
                               name="time"/>
                        <ErrorMessage name="time" component="span" className="text-danger"></ErrorMessage>
                    </div>
                    <div className="form-group">
                        <button type="submit" className="btn btn-block create-account">Đăng ký</button>
                    </div>
                </Form>
            </Formik>
            <ToastContainer/>
        </div>
    )
}

export default SongCreate;
